// The document goes on to explain that each button to be pressed can be found by starting on the previous button and moving to adjacent buttons on the keypad: U moves up, D moves down, L moves left, and R moves right. Each line of instructions corresponds to one button, starting at the previous button (or, for the first line, the "5" button); press whatever button you're on at the end of each line. If a move doesn't lead to a button, ignore it.
//
// Your puzzle input is the instructions from the document you found at the front desk. What is the bathroom code?

const directions = ',,,,'.split(',').map((direction) => direction.split(''))

const funcs = {
  U: moveUp,
  D: moveDown,
  L: moveLeft,
  R: moveRight
}

let keyPad = []
keyPad.push([false, false, 1, false, false])
keyPad.push([false, 2, 3, 4, false])
keyPad.push([5,6,7,8,9])
keyPad.push([false, "A", "B", "C", false])
keyPad.push([false, false, "D", false, false])

let currentCoords = [2,0]
let password = []

directions.forEach((directionSet) => {
  directionSet.forEach((direction) => {
   currentCoords = funcs[direction](currentCoords)
  })
  password.push(findKey(currentCoords))
})

console.log(password.join())

function findKey(coords) {
   return keyPad[coords[0]][coords[1]]
}

function moveUp(coords) {
  let test = [coords[0]-1, coords[1]]
  return coords[0] === 0 || !findKey(test) ? coords : test
}

function moveDown(coords) {
  let test = [coords[0]+1, coords[1]]
  return coords[0] === 4 || !findKey(test) ? coords : test
}

function moveLeft(coords) {
  let test = [coords[0], coords[1]-1]
  return coords[1] === 0 || !findKey(test) ? coords : test
}

function moveRight(coords) {
  let test = [coords[0], coords[1]+1]
  return coords[1] === 4 || !findKey(test) ? coords : test
}
